import React from "react";

import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";

import { format } from "date-fns";
import ptBR from "date-fns/locale/pt-BR";

const useStyles = makeStyles(() => ({
  root: {
    width: 285,
    minHeight: 255,
  },
  cardHeader: {
    width: 285,
    minHeight: 255,
  },
}));

const ClassificadoItem = ({ titulo, dataCadastro, descricao }) => {
  const classes = useStyles();

  const date = new Date(dataCadastro);
  const formattedDate = format(date, "'Dia' dd 'de' MMMM', às ' HH:mm'h'", {
    locale: ptBR,
  });

  return (
    <Card className={classes.root}>
      <CardHeader
        className={classes.cardHeader}
        title={titulo}
        subheader={formattedDate}
        align="center"
      />
      <CardContent>
        <Typography align="justify">{descricao}</Typography>
      </CardContent>
    </Card>
  );
};

export default ClassificadoItem;
