import React from "react";
import { Formik, Form, Field } from "formik";
import { TextField } from "formik-material-ui";

import { makeStyles } from "@material-ui/core/styles";
import { Button, LinearProgress } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  form: {
    display: "flex",
    flexDirection: "column",
    margin: "auto",
    width: "fit-content",
  },
  formControl: {
    marginTop: theme.spacing(2),
    minWidth: 120,
  },
  formControlLabel: {
    marginTop: theme.spacing(1),
  },
}));

const validate = (values) => {
  const errors = {};
  if (!values.titulo) {
    errors.titulo = "Obrigatório";
  } else if (values.titulo.length > 20) {
    errors.titulo = "Máximo de 20 caracteres";
  }

  if (!values.descricao) {
    errors.descricao = "Obrigatório";
  } else if (values.descricao.length > 100) {
    errors.descricao = "Máximo de 100 caracteres";
  }

  return errors;
};

const AddClassificadoForm = () => {
  const classes = useStyles();

  return (
    <Formik
      initialValues={{
        titulo: "",
        descricao: "",
      }}
      validate={validate}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          setSubmitting(false);
          alert(JSON.stringify(values, null, 2));
        }, 500);
      }}
    >
      {({ submitForm, isSubmitting }) => (
        <Form>
          <Field
            component={TextField}
            name="titulo"
            type="text"
            label="Título"
          />
          <br />
          <Field
            component={TextField}
            multiline
            name="descricao"
            type="text"
            label="Descrição"
          />
          {isSubmitting && <LinearProgress />}
          <br />
          <Button
            variant="contained"
            color="primary"
            disabled={isSubmitting}
            onClick={submitForm}
          >
            Submit
          </Button>
        </Form>
      )}
    </Formik>
  );
};

export default AddClassificadoForm;
