import { createSelector } from "reselect";

const selectClassificado = (state) => state.classificados;

export const selectClassificados = createSelector(
  [selectClassificado],
  (classificados) => classificados.classificado
);

/* export const selectClassificadosForPreview = createSelector(
  [selectClassificados],
  (classificado) =>
    classificado
      ? Object.keys(classificado).map((key) => classificado[key])
      : []
); */

export const selectClassificadosForPreview = createSelector(
  [selectClassificados],
  (classificado) => classificado
);

export const selectIsClassificadosFetching = createSelector(
  [selectClassificado],
  (classificado) => classificado.isFetching
);

export const selectIsClassificadosLoaded = createSelector(
  [selectClassificado],
  (classificado) => !!classificado.classificados
);
