import React from "react";

import { makeStyles } from "@material-ui/core/styles";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";

import AddClassificadoForm from "../add-classificado-form/add-classificado-form";

const useStyles = makeStyles((theme) => ({
  form: {
    display: "flex",
    flexDirection: "column",
    margin: "auto",
    width: "fit-content",
  },
  formControl: {
    marginTop: theme.spacing(2),
    minWidth: 120,
  },
  formControlLabel: {
    marginTop: theme.spacing(1),
  },
}));

const AddClassificadoDialog = ({ open, setOpen }) => {
  const classes = useStyles();

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Dialog
      open={open}
      onClose={handleClose}
      fullWidth={true}
      maxWidth="md"
      aria-labelledby="form-dialog-novo-classificado"
    >
      <DialogTitle id="form-dialog-novo-classificado">
        Novo Classificado
      </DialogTitle>
      <DialogContent>
        <DialogContentText>
          Complete os com os seguintes dados para adicionar um novo
          classificado.
        </DialogContentText>
        <AddClassificadoForm />
      </DialogContent>
    </Dialog>
  );
};

export default AddClassificadoDialog;
