import React from "react";

import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import { red } from "@material-ui/core/colors";

import { format } from "date-fns";
import ptBR from "date-fns/locale/pt-BR";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 250,
    height: 250,
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
}));

const ClassificadoItem = ({ titulo, dataCadastro, descricao }) => {
  const classes = useStyles();

  const date = new Date(dataCadastro);
  const formattedDate = format(date, "'Dia' dd 'de' MMMM', às ' HH:mm'h'", {
    locale: ptBR,
  });

  return (
    <Card className={classes.root}>
      <CardHeader title={titulo} subheader={formattedDate} />
      <CardContent>
        <Typography>{descricao}</Typography>
      </CardContent>
    </Card>
  );
};

export default ClassificadoItem;
