import React from "react";
import { Formik, Form, Field } from "formik";
import { TextField } from "formik-material-ui";

import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import LinearProgress from "@material-ui/core/LinearProgress";

const useStyles = makeStyles((theme) => ({
  form: {
    display: "flex",
    flexDirection: "column",
    width: "fit-content",
    minWidth: 120,
    "& > *": {
      margin: theme.spacing(1, 2, 2, 2),
    },
  },
  formInput: {
    flexDirection: "row",
  },
  formButton: {
    width: "20%",
  },
}));

const validate = (values) => {
  const errors = {};
  if (!values.titulo) {
    errors.titulo = "Obrigatório";
  } else if (values.titulo.length > 20) {
    errors.titulo = "Máximo de 20 caracteres";
  }

  if (!values.descricao) {
    errors.descricao = "Obrigatório";
  } else if (values.descricao.length > 100) {
    errors.descricao = "Máximo de 100 caracteres";
  }

  return errors;
};

const AddClassificadoForm = () => {
  const classes = useStyles();

  return (
    <Formik
      initialValues={{
        titulo: "",
        descricao: "",
      }}
      validate={validate}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          setSubmitting(false);
          alert(JSON.stringify(values, null, 2));
        }, 500);
      }}
    >
      {({ submitForm, isSubmitting }) => (
        <Form className={classes.form}>
          <Box className={classes.formInput} mb={2}>
            <Field
              component={TextField}
              name="titulo"
              type="text"
              label="Título"
            />
            <Field
              component={TextField}
              multiline
              name="descricao"
              type="text"
              label="Descrição"
            />
          </Box>
          {isSubmitting && <LinearProgress />}
          <Box className={classes.formButton}>
            <Button
              variant="contained"
              color="primary"
              disabled={isSubmitting}
              onClick={submitForm}
            >
              Submit
            </Button>
          </Box>
        </Form>
      )}
    </Formik>
  );
};

export default AddClassificadoForm;
