import { configureStore } from "@reduxjs/toolkit";
import { createLogger } from "redux-logger";

import rootReducer from "./root-reducer";

const logger = createLogger({
  diff: true,
});
export const store = configureStore({
  reducer: rootReducer,
  middleware: logger,
});

export default store;
