import React, { useState } from "react";

import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";

import { selectSnackbarOpen } from "../../redux/snackbar/snackbar.selectors";

const AddClassificadoSnackbar = (open) => {
  const [openSnackbar, setOpenSnackbar] = useState(false);

  setOpenSnackbar(!open);

  const handleClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }

    setOpenSnackbar(false);
  };

  return (
    <Snackbar open={openSnackbar} autoHideDuration={5000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success" variant="filled">
        Classificado adicionado!
      </Alert>
    </Snackbar>
  );
};

const mapStateToProps = createStructuredSelector({
  snackbar: selectSnackbarOpen,
});

export default AddClassificadoSnackbar;
